import React from 'react';
import './Recipe.css';

function Recipe(props) {
  const { name, image, description, ingredients, makingProcess } = props.data;

  return (
    <div className="recipe">
        <div className="recipe-info">
            <img className="recipe-photo" src={image} alt="recipe" />
            <div className="recipe-name">{name}</div>
        </div>
        <ul className="recipe-attributes">
            <li><div className='attribute'><strong>תיאור:&nbsp; </strong> {description}</div></li>
            <li><div className='attribute'><strong>מצרכים:&nbsp; </strong> {ingredients}</div></li>
            <li><div className='attribute'><strong>אופן ההכנה:&nbsp; </strong> {makingProcess}</div></li>
        </ul>
    </div>
  );
}

export default Recipe;
