import React from 'react';
import './Recipe.css';

function Recipe(props) {
  const { name, image, description, ingredients, makingProcess } = props.data;

  return (
    <div class="recipe-container">
      <div class="recipe-microwave"></div>
      <div class="recipe-info">
        <div class="recipe-ingredients">
          <h3>Ingredients:</h3>
          <ul>
            {props.ingredients.map((ingredient) => (
              <li>{ingredient}</li>
            ))}
          </ul>
        </div>
        <div class="recipe-making">
          <h3>Making:</h3>
          <ol>
            {props.making.map((step) => (
              <li>{step}</li>
            ))}
          </ol>
        </div>
      </div>
    </div>

  );
}

export default Recipe;
